INSERT INTO manicure_service (name, description, price, duration_minutes) VALUES
  ('Класически маникюр', 'Класическият маникюр включва оформяне на ноктите, почистване на кожичките, полиране и нанасяне на лак. Придава изчистен вид на ръцете, подхранва и заздравява ноктите. Подходящ за всеки повод и подчертава естествената красота.', 25.00, 45),
  ('Гел лак', 'Гел лакът е дълготрайно решение за перфектен маникюр с издръжливост до 2-3 седмици. Предлага богата гама от цветове и блестящ ефект без напукване или белене. Идеален избор за заети дами с безупречни нокти.', 35.00, 60),
  ('Поддръжка гел', 'Поддръжката на гел включва корекция на отрасналата част и подсилване на покритието. Осигурява продължителна красота и здравина на ноктите, запазвайки перфектния им вид. Препоръчва се на всеки 2-3 седмици за оптимални резултати.', 40.00, 75),
  ('Изграждане', 'Изграждането на нокти с гел или акрил е идеално за удължаване и подсилване на естествените нокти. Предлага изключителна издръжливост и възможност за разнообразен дизайн. Перфектно за дълги и здрави нокти с безупречен вид.', 50.00, 90),
  ('Педикюр', 'Педикюрът е задължителна грижа за краката, която включва почистване, оформяне на ноктите и нанасяне на лак. Процедурата подпомага релаксацията, подобрява вида и заздравява кожата. Подходящ за всеки сезон и повод, оставя краката свежи.', 30.00, 60),
  ('Японски маникюр', 'Японският маникюр P-Shine е уникална терапия за естествени нокти с полиране и минерални прахове. Постига естествен блясък и заздравяване без използване на лак. Перфектен избор за тези, които предпочитат натурална красота и грижа.', 45.00, 60);
