INSERT INTO product (name, description, price, image_url, stock) VALUES
  ('Гел лак OPI - Червено', 'Професионален гел лак с дълготрайност до 3 седмици. Интензивен червен цвят с блясък.', 45.00, '/img/shop-product1.jpg', 15),
  ('Гел лак OPI - Розово', 'Професионален гел лак с дълготрайност до 3 седмици. Нежен розов цвят за елегантен вид.', 45.00, '/img/shop-product2.jpg', 20),
  ('Масло за кутикули', 'Подхранващо масло за грижа на кожичките. Съдържа витамин E и ефирни масла.', 18.00, '/img/shop-product3.jpg', 30),
  ('Крем за ръце', 'Хидратиращ крем за ръце с шеа масло. Подхранва и омекотява кожата.', 25.00, '/img/shop-product4.jpg', 25),
  ('Набор, пили за нокти', 'Професионален набор от 5 пили за оформяне на нокти. Различна гранулация.', 22.00, '/img/shop-product5.jpg', 40),
  ('База за гел лак', 'Професионална база за гел лак. Подсилва и защитава естествения нокът.', 35.00, '/img/shop-product6.jpg', 18),
  ('Топ за гел лак', 'Професионален топ коут за гел лак. Осигурява дълготрайност и блясък.', 35.00, '/img/shop-product7.jpg', 18),
  ('Декорация за нокти', 'Сет от диамантчета в различни цветове и размери за дизайн на нокти.', 28.00, '/img/shop-product8.jpg', 35);
